          originalPreview: result.originalText.substring(0, 40) + '...',
          optimizedPreview: result.optimizedPrompt.substring(0, 40) + '...',
          score: result.score,
          fullAnalysis: result
        };
        setHistory(prev => [newItem, ...prev].slice(0, 20));
