const replaceVariables = (text: string, vars: Record<string, string>) => {
  let result = text;
  Object.entries(vars).forEach(([key, value]) => {
    const val = value || `[MISSING_${key}]`;
    const regex = new RegExp(`\\[${key}\\]|\\{\\{${key}\\}\\}|<${key}>`, 'gi');
    result = result.replace(regex, val);
  });
  return result;
};

